import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Testar conexão com Django
    axios.get('/api/status/')
      .then(response => {
        setMessage(response.data.status);
        setLoading(false);
      })
      .catch(error => {
        console.error('Erro ao conectar com Django:', error);
        setMessage('Erro: Django não está a responder');
        setLoading(false);
      });
  }, []);

  return (
    <div style={{ 
      padding: '50px', 
      textAlign: 'center', 
      fontFamily: 'Arial, sans-serif' 
    }}>
      <h1>React + Django</h1>
      
      {loading ? (
        <p>A testar conexão...</p>
      ) : (
        <div>
          <h2 style={{ 
            color: message.includes('sucesso') ? 'green' : 'red' 
          }}>
            {message}
          </h2>
          
          {message.includes('sucesso') && (
            <div>
              <p>✅ Conexão estabelecida com sucesso!</p>
              <p>Podes agora começar a desenvolver a tua aplicação.</p>
            </div>
          )}
        </div>
      )}
      
      <div style={{ marginTop: '30px', fontSize: '14px', color: '#666' }}>
        <p><strong>Frontend:</strong> http://localhost:3000</p>
        <p><strong>Backend:</strong> http://localhost:8000</p>
        <p><strong>API Status:</strong> http://localhost:8000/api/status/</p>
      </div>
    </div>
  );
}

export default App;
